mapper.insert:
INSERT INTO notices (
	user_id, subject, reg_date, ref, re_step, re_level, content, readcount, num
) VALUES (
	#{user_id}, #{subject}, #{reg_date}, #{ref}, #{re_step}, #{re_level}, #{content}, 0, seq_notices.NEXTVAL
)

mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, A.* FROM (
		SELECT num, user_id, subject, reg_date, readcount, ref, re_step, re_level, content
		FROM notices
		ORDER BY ref DESC, re_step ASC
	) A
	WHERE ROWNUM <= #{end}
)
WHERE rnum >= #{start}

SELECT * FROM (
	SELECT ROWNUM rnum, A.* FROM (
		SELECT num, user_id, subject, reg_date, readcount, ref, re_step, re_level, content
		FROM notices
		WHERE user_id = #{user_id}
		ORDER BY ref DESC, re_step ASC
	) A
	WHERE ROWNUM <= #{end}
)
WHERE rnum >= #{start}

SELECT num, user_id, subject, reg_date, readcount, ref, re_step, re_level, content
FROM notices
WHERE num = #{num}

mapper.update:
UPDATE notices
SET subject = #{subject}, content = #{content}
WHERE num = #{num}
